import{_ as a,c as l,o as t,b0 as o}from"./chunks/framework.w6NQj85O.js";const m=JSON.parse('{"title":"🚩 标志","description":"","frontmatter":{},"headers":[],"relativePath":"DecentHolograms/general.flags.md","filePath":"DecentHolograms/general.flags.md"}'),d={name:"DecentHolograms/general.flags.md"};function r(c,e,i,n,g,h){return t(),l("div",null,e[0]||(e[0]=[o('<h1 id="🚩-标志" tabindex="-1">🚩 标志 <a class="header-anchor" href="#🚩-标志" aria-label="Permalink to &quot;🚩 标志&quot;">​</a></h1><p>标志的通用用法</p><p>在 DecentHolograms 中，你可以配置悬浮字及悬浮字行上的多个标志。这些标志可以禁用一些你不需要的功能。</p><div class="info custom-block"><p class="custom-block-title">信息</p><p>在悬浮字上的标志会影响到所有行，但行也可单独设置标志以防止影响到其他行的悬浮字。</p></div><h2 id="标志列表" tabindex="-1">标志列表 <a class="header-anchor" href="#标志列表" aria-label="Permalink to &quot;标志列表&quot;">​</a></h2><p>这些是当前版本中可使用的标志：</p><ul><li><code>DISABLE_UPDATING</code> - 禁用当前行或整个悬浮字的更新。</li><li><code>DISABLE_PLACEHOLDERS</code> - 禁用当前行或整个悬浮字的变量显示。</li><li><code>DISABLE_ANIMATIONS</code> - 禁用当前行或整个悬浮字的动画。</li><li><code>DISABLE_ACTIONS</code> - 禁用当前行或整个悬浮字的点击动作。</li></ul><h2 id="相关命令" tabindex="-1">相关命令 <a class="header-anchor" href="#相关命令" aria-label="Permalink to &quot;相关命令&quot;">​</a></h2><p>这些命令可以用来为整个或单行悬浮字设置标志：</p><ul><li><a href="./general.commands.hologram.html#dh-h-addflag-悬浮字-标志"><code>/dh h addflag &lt;悬浮字&gt; &lt;标志&gt;</code></a></li><li><a href="./general.commands.hologram.html#dh-h-removeflag-悬浮字-标志"><code>/dh h removeflag &lt;悬浮字&gt; &lt;标志&gt;</code></a></li><li><a href="./general.commands.hologram-line.html#dh-l-addflag-悬浮字-页码-行号-标志"><code>/dh l addflag &lt;悬浮字&gt; &lt;页码&gt; &lt;行号&gt; &lt;标志&gt;</code></a></li><li><a href="./general.commands.hologram-line.html#dh-l-removeflag-悬浮字-页码-行号-标志"><code>/dh l removeflag &lt;悬浮字&gt; &lt;页码&gt; &lt;行号&gt; &lt;标志&gt;</code></a></li></ul>',10)]))}const f=a(d,[["render",r]]);export{m as __pageData,f as default};
