import{_ as d,c as r,o as e,b0 as a}from"./chunks/framework.w6NQj85O.js";const u=JSON.parse('{"title":"附魔列表","description":"","frontmatter":{},"headers":[],"relativePath":"ExcellentEnchants/general.enchantments.md","filePath":"ExcellentEnchants/general.enchantments.md"}'),h={name:"ExcellentEnchants/general.enchantments.md"};function n(o,t,b,i,l,s){return e(),r("div",null,t[0]||(t[0]=[a('<h1 id="附魔列表" tabindex="-1">附魔列表 <a class="header-anchor" href="#附魔列表" aria-label="Permalink to &quot;附魔列表&quot;">​</a></h1><p>冲突附魔的行为可根据配置文件的决定而修改/移除。</p><h2 id="盔甲附魔" tabindex="-1">盔甲附魔 <a class="header-anchor" href="#盔甲附魔" aria-label="Permalink to &quot;盔甲附魔&quot;">​</a></h2><table tabindex="0"><thead><tr><th>附魔名称</th><th>附魔描述</th><th>作用物品</th><th>冲突附魔</th></tr></thead><tbody><tr><td>海王<br>（Aquaman）</td><td>获得水下呼吸效果。</td><td>头盔</td><td>-</td></tr><tr><td>灵跃<br>（Bunny Hop）</td><td>获得跳跃提升效果。</td><td>靴子</td><td>-</td></tr><tr><td>霜甲<br>（Cold Steel）</td><td>给予攻击者挖掘疲劳效果。</td><td>胸甲</td><td>-</td></tr><tr><td>暗御<br>（Darkness Cloak）</td><td>给予攻击者黑暗效果。</td><td>胸甲</td><td>-</td></tr><tr><td>元素之御<br>（Elemental Protection）</td><td>降低中毒、魔法、凋零、闪电及冰冻伤害。</td><td>所有盔甲</td><td>-</td></tr><tr><td>炽炎行者<br>（Flame Walker）</td><td>与原版的冰霜行者类似，行走在岩浆上可产生临时的岩浆块以防止玩家坠入岩浆，同时免疫岩浆块的灼烧伤害。</td><td>靴子</td><td>冰霜行者</td></tr><tr><td>炽炎之盾<br>（Fire Shield）</td><td>与荆棘相似，对攻击者施加着火。</td><td>胸甲</td><td>-</td></tr><tr><td>霜寒之盾<br>（Ice Shield）</td><td>冻结并降低敌人移速。</td><td>胸甲</td><td>-</td></tr><tr><td>硬化<br>（Hardened）</td><td>受击后获得抗性提升效果。</td><td>胸甲</td><td>-</td></tr><tr><td>夜视<br>（Night Vision）</td><td>给予夜视效果。</td><td>头盔</td><td>-</td></tr><tr><td>再生<br>（Regrowth）</td><td>无视饱食度与饱和度随时间缓慢恢复生命值。</td><td>胸甲</td><td>-</td></tr><tr><td>饱食<br>（Saturation）</td><td>随时间缓慢恢复饱食度与饱和度。</td><td>头盔</td><td>-</td></tr><tr><td>自爆<br>（Self-Destruction）</td><td>死亡后产生爆炸。</td><td>胸甲</td><td>-</td></tr><tr><td>疾步<br>（Sonic）</td><td>获得速度效果。</td><td>靴子</td><td>-</td></tr><tr><td>坚挺<br>（Stopping Force）</td><td>受伤时增加击退抗性。</td><td>护腿</td><td>-</td></tr><tr><td>寻宝<br>（Treasure Hunter）</td><td>几率在要塞等建筑结构的宝箱中获得更多战利品。</td><td>头盔</td><td>-</td></tr></tbody></table><h2 id="弓类附魔" tabindex="-1">弓类附魔 <a class="header-anchor" href="#弓类附魔" aria-label="Permalink to &quot;弓类附魔&quot;">​</a></h2><table tabindex="0"><thead><tr><th>附魔名称</th><th>附魔描述</th><th>作用物品</th><th>冲突附魔</th></tr></thead><tbody><tr><td>爆裂之矢<br>（Bomber）</td><td>概率将射出的箭替换成 TNT。</td><td>弓、弩</td><td>飞火之矢、末影之矢、恶魂之矢（所有箭类特殊附魔）</td></tr><tr><td>末影之矢<br>（Ender Bow）</td><td>概率将射出的箭替换成末影珍珠。</td><td>弓、弩</td><td>恶魂之矢、爆裂之矢、飞火之矢</td></tr><tr><td>恶魂之矢<br>（Ghast）</td><td>概率将射出的箭替换成恶魂的烈焰弹。</td><td>弓、弩</td><td>末影之矢、爆裂之矢、飞火之矢</td></tr><tr><td>飞火之矢<br>（Flare）</td><td>概率在箭落地时放置一根火把。</td><td>弓、弩</td><td>-</td></tr><tr><td>乘风快矢<br>（Sniper）</td><td>提升箭矢的速度。</td><td>弓、弩</td><td>-</td></tr><tr><td>虚浮之矢<br>（Hover）</td><td>命中目标后概率施加漂浮效果。</td><td>弓、弩</td><td>末影之矢、恶魂之矢、爆裂之矢</td></tr><tr><td>惑乱之矢<br>（Confusing Arrows）</td><td>命中目标后概率施加反胃效果。</td><td>弓、弩</td><td>末影之矢、恶魂之矢、爆裂之矢</td></tr><tr><td>龙涎之矢<br>（Dragonfire Arrows）</td><td>概率在箭落地时生成一团龙息。</td><td>弓、弩</td><td>末影之矢、恶魂之矢、爆裂之矢</td></tr><tr><td>暗黑之矢<br>（Darkness Arrows）</td><td>命中目标后概率施加黑暗效果。</td><td>弓、弩</td><td>末影之矢、恶魂之矢、爆裂之矢</td></tr><tr><td>唤雷之矢<br>（Electrified Arrows）</td><td>命中目标后概率生成闪电。</td><td>弓、弩</td><td>末影之矢、恶魂之矢、爆裂之矢</td></tr><tr><td>爆破之矢<br>（Explosive Arrows）</td><td>概率射出落地爆炸的箭。</td><td>弓、弩</td><td>末影之矢、恶魂之矢、爆裂之矢</td></tr><tr><td>淬毒之矢<br>（Poisoned Arrows）</td><td>命中目标后概率施加中毒。</td><td>弓、弩</td><td>末影之矢、恶魂之矢、爆裂之矢</td></tr><tr><td>歃血之矢<br>（Vampiric Arrows）</td><td>命中目标后概率恢复生命值。</td><td>弓、弩</td><td>末影之矢、恶魂之矢、爆裂之矢</td></tr><tr><td>凋谢之矢<br>（Withered Arrows）</td><td>命中目标后概率施加凋零。</td><td>弓、弩</td><td>末影之矢、恶魂之矢、爆裂之矢</td></tr></tbody></table><h2 id="工具附魔" tabindex="-1">工具附魔 <a class="header-anchor" href="#工具附魔" aria-label="Permalink to &quot;工具附魔&quot;">​</a></h2><table tabindex="0"><thead><tr><th>附魔名称</th><th>附魔描述</th><th>作用物品</th><th>冲突附魔</th></tr></thead><tbody><tr><td>爆破发掘<br>（Blast Mining）</td><td>概率在挖掘时产生爆炸并破坏范围内所有方块。</td><td>镐</td><td>穿隧、连锁挖掘</td></tr><tr><td>封魔之触<br>（Divine Touch）</td><td>概率挖下刷怪笼。</td><td>镐</td><td>熔炼</td></tr><tr><td>急迫<br>（Haste）</td><td>概率在挖掘时产生急迫效果。</td><td>任意工具</td><td>-</td></tr><tr><td>幸运矿工<br>（Lucky Miner）</td><td>概率从挖掘的方块中获取更多经验值。</td><td>镐</td><td>-</td></tr><tr><td>复种<br>（Replanter）</td><td>右键或破坏作物时自动补种。</td><td>锄</td><td>-</td></tr><tr><td>搬箱<br>（Silk Chest）</td><td>破坏箱子后有几率使箱子保留内容物而不掉落。</td><td>斧</td><td>-</td></tr><tr><td>熔炼<br>（Smelter）</td><td>概率自动熔炼挖下的矿物。</td><td>任意工具</td><td>精准采集、封魔之触</td></tr><tr><td>心灵遥感<br>（Telekinesis）</td><td>概率将掉落物直接送入背包。</td><td>任意工具</td><td>-</td></tr><tr><td>穿隧<br>（Tunnel）</td><td>概率范围挖掘。I 级为 1x2 范围，II 级为 2x2，III 级为 3x3。</td><td>镐、铲</td><td>连锁挖掘、爆破发掘</td></tr><tr><td>连锁挖掘<br>（Veinminer）</td><td>概率一次挖下所有相邻的矿物。</td><td>镐</td><td>穿隧、爆破发掘</td></tr></tbody></table><h2 id="鱼竿附魔" tabindex="-1">鱼竿附魔 <a class="header-anchor" href="#鱼竿附魔" aria-label="Permalink to &quot;鱼竿附魔&quot;">​</a></h2><table tabindex="0"><thead><tr><th>附魔名称</th><th>附魔描述</th><th>作用物品</th><th>冲突附魔</th></tr></thead><tbody><tr><td>自动收杆<br>（Auto Reel）</td><td>在鱼咬钩时自动收杆。</td><td>钓鱼竿</td><td>-</td></tr><tr><td>二重钓<br>（Double Catch）</td><td>钓上来的物品数量翻倍。</td><td>钓鱼竿</td><td>-</td></tr><tr><td>捕鱼大师<br>（Seasoned Angler）</td><td>提升钓鱼获得的经验数量。</td><td>钓鱼竿</td><td>-</td></tr><tr><td>烈焰鱼钩<br>（Survivalist）</td><td>自动将钓上来的食物转化为烤熟的版本。</td><td>钓鱼竿</td><td>-</td></tr><tr><td>抛钩大师<br>（River Master）</td><td>提升抛钩距离。</td><td>钓鱼竿</td><td>-</td></tr><tr><td>溺尸诅咒<br>（Curse Of Drowned）</td><td>概率钓上溺尸。</td><td>钓鱼竿</td><td>-</td></tr></tbody></table><h2 id="武器附魔" tabindex="-1">武器附魔 <a class="header-anchor" href="#武器附魔" aria-label="Permalink to &quot;武器附魔&quot;">​</a></h2><table tabindex="0"><thead><tr><th>附魔名称</th><th>附魔描述</th><th>作用物品</th><th>冲突附魔</th></tr></thead><tbody><tr><td>下界杀手（Bane of Netherspawn）</td><td>概率对下界生物造成额外伤害。</td><td>剑、斧</td><td>锋利、亡灵杀手、节肢杀手、村庄卫士</td></tr><tr><td>致盲（Blindness）</td><td>命中时概率附加失明效果。</td><td>剑、斧</td><td>-</td></tr><tr><td>混乱（Confusion）</td><td>命中时概率附加反胃效果。</td><td>剑、斧</td><td>-</td></tr><tr><td>治愈（Cure）</td><td>命中时概率治愈僵尸村民与僵尸猪灵。</td><td>剑、斧</td><td>-</td></tr><tr><td>唤死诅咒（Curse of Death）</td><td>若击杀玩家，武器持有者也有概率被杀死。</td><td>剑、斧</td><td>-</td></tr><tr><td>剥蚀（Cutter）</td><td>概率除去敌方防具并使其额外减少耐久。</td><td>剑、斧</td><td>-</td></tr><tr><td>斩首（Decapitator）</td><td>被击杀的敌怪概率掉落头颅。</td><td>剑、斧</td><td>-</td></tr><tr><td>复击（Double Strike）</td><td>概率造成双倍伤害。</td><td>剑、斧</td><td>-</td></tr><tr><td>饥饿（Exhaust）</td><td>命中时概率附加饥饿效果。</td><td>剑、斧</td><td>-</td></tr><tr><td>经验（Exp Hunter）</td><td>击杀的敌怪有几率掉落更多经验值。</td><td>剑、斧</td><td>-</td></tr><tr><td>霜寒之刃（Ice Aspect）</td><td>命中时概率附加缓慢效果。</td><td>剑、斧</td><td>-</td></tr><tr><td>淬火之戟（Infernus）</td><td>概率投出附带火焰的三叉戟。</td><td>三叉戟</td><td>-</td></tr><tr><td>掠夺（Nimble）</td><td>击杀敌怪的掉落物有几率直接进入背包。</td><td>剑、斧</td><td>-</td></tr><tr><td>瘫痪（Paralyze）</td><td>命中时有概率附加挖掘疲劳效果。</td><td>剑、斧</td><td>-</td></tr><tr><td>怒气（Rage）</td><td>命中时概率获得力量效果。</td><td>剑、斧</td><td>-</td></tr><tr><td>升空（Rocket）</td><td>命中时概率将敌怪击飞。</td><td>剑、斧</td><td>-</td></tr><tr><td>拾荒（Scavenger）</td><td>击杀敌怪有概率获得额外掉落物。</td><td>剑、斧</td><td>-</td></tr><tr><td>惊喜（Surprise）</td><td>命中时概率附加随机效果。</td><td>剑、斧</td><td>-</td></tr><tr><td>吸血（Swiper）</td><td>命中时概率窃取玩家生命值。</td><td>剑、斧</td><td>-</td></tr><tr><td>顽抗（Temper）</td><td>损失生命值越多，造成的伤害越高。</td><td>剑、斧</td><td>-</td></tr><tr><td>猎捕（Thrifty）</td><td>概率将击杀的敌怪转化为刷怪蛋。</td><td>剑、斧</td><td>-</td></tr><tr><td>落雷（Thunder）</td><td>命中时概率召唤闪电。</td><td>剑、斧</td><td>-</td></tr><tr><td>歃血（Vampire）</td><td>命中时概率恢复生命值。</td><td>剑、斧</td><td>-</td></tr><tr><td>淬毒（Venom）</td><td>命中时概率附加中毒效果。</td><td>剑、斧</td><td>-</td></tr><tr><td>村庄卫士（Village Defender）</td><td>概率对灾厄村民造成额外伤害。</td><td>剑、斧</td><td>锋利、亡灵杀手、节肢杀手、下界杀手</td></tr><tr><td>凋零（Wither）</td><td>命中时概率附加凋零效果。</td><td>剑、斧</td><td>-</td></tr></tbody></table><h2 id="通用附魔" tabindex="-1">通用附魔 <a class="header-anchor" href="#通用附魔" aria-label="Permalink to &quot;通用附魔&quot;">​</a></h2><table tabindex="0"><thead><tr><th>附魔名称</th><th>附魔描述</th><th>作用物品</th><th>冲突附魔</th></tr></thead><tbody><tr><td>传承<br>（Restore）</td><td>物品损坏时以失去所有附魔的代价阻止损坏并回复一定耐久度。</td><td>所有物品</td><td>-</td></tr><tr><td>归魂<br>（Soulbound）</td><td>阻止物品的死亡掉落。</td><td>所有物品</td><td>消失诅咒</td></tr><tr><td>锈斑诅咒<br>（Curse of Breaking）</td><td>几率在使用工具时花费额外的耐久度。</td><td>所有物品</td><td>耐久</td></tr><tr><td>祛魔诅咒<br>（Curse of Mediocrity）</td><td>概率消除从玩家/实体身上掉落物品/方块的附魔。</td><td>任意工具、武器</td><td>-</td></tr><tr><td>厄运诅咒<br>（Curse of Misfortune）</td><td>概率导致实体不产生掉落物。</td><td>任意工具、武器</td><td>时运、抢夺</td></tr><tr><td>脆弱诅咒<br>（Curse of Fragility）</td><td>物品不能在砂轮或铁砧中使用。</td><td>所有物品</td><td>-</td></tr></tbody></table>',14)]))}const f=d(h,[["render",n]]);export{u as __pageData,f as default};
